var mysql = require('easymysql');
var settings = require('../settings');
var conn = mysql.create({
    'maxconnections' : 10
});
conn.addserver({
    host: settings.host,
    user: settings.user,
    password: settings.password,
    database: settings.db,
    port: settings.port
});
module.exports = conn;
